// vite.config.js
import { defineConfig } from 'vite';
import vue from '@vitejs/plugin-vue';
import vuetify from 'vite-plugin-vuetify';
import path from 'path'; // <-- LÍNEA AÑADIDA

export default defineConfig({
  plugins: [
    vue(),
    vuetify({
      autoImport: true,
      // --- SECCIÓN AÑADIDA ---
      // Le decimos explícitamente al plugin dónde está nuestro archivo de estilos
      // para evitar problemas con las rutas absolutas en Windows.
      styles: {
        configFile: 'src/styles/settings.scss',
      },
    }),
  ],
  // --- SECCIÓN AÑADIDA (Mejor práctica) ---
  // Definimos un "alias" para que, cuando escribamos '@' en una ruta de importación,
  // el sistema sepa que nos referimos a la carpeta 'src/'.
  // Esto hace las importaciones más limpias y consistentes.
  resolve: {
    alias: {
      '@': path.resolve(__dirname, 'src'),
    },
  },
  server: {
    port: 5173,
  },
});